// Updated src/components/VirtualTourUploadPage.js with Camera Option

import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate, useParams } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';
import api from '../utils/api';
import toast from 'react-hot-toast';
import Camera360Capture from './Camera360Capture';

const VirtualTourUploadPage = () => {
  const { propertyId } = useParams();
  const navigate = useNavigate();
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [sceneName, setSceneName] = useState('Main Room');
  const [uploadedFile, setUploadedFile] = useState(null);
  const [showCamera, setShowCamera] = useState(false);

  // ... (keep existing onDrop and handleUpload functions)

  const handleCameraCapture = (file) => {
    setUploadedFile(file);
    setShowCamera(false);
    toast.success('Image captured! Ready to upload.');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white py-12">
      <AnimatePresence>
        {showCamera && (
          <Camera360Capture
            onCapture={handleCameraCapture}
            onClose={() => setShowCamera(false)}
          />
        )}
      </AnimatePresence>

      {/* ... existing header ... */}

      <motion.div className="glass rounded-xl p-8">
        {/* Scene Name Input */}
        <div className="mb-6">
          <label className="block text-sm font-medium mb-2">Scene Name</label>
          <input
            type="text"
            value={sceneName}
            onChange={(e) => setSceneName(e.target.value)}
            placeholder="e.g., Living Room, Master Bedroom"
            className="w-full px-4 py-3 bg-white/10 rounded-lg border border-white/20 focus:border-purple-400 focus:outline-none"
            disabled={uploading}
          />
        </div>

        {/* Camera or Upload Options */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <button
            onClick={() => setShowCamera(true)}
            className="p-6 border-2 border-dashed border-purple-400 rounded-xl hover:bg-purple-500/10 transition-all"
            disabled={uploading}
          >
            <div className="text-4xl mb-2">📸</div>
            <div className="font-semibold">Use Camera</div>
            <div className="text-sm text-purple-200">Capture 360° now</div>
          </button>

          <div {...getRootProps()} className={`
            p-6 border-2 border-dashed border-white/30 rounded-xl
            hover:border-white/50 hover:bg-white/5 transition-all cursor-pointer
            ${isDragActive ? 'border-purple-400 bg-purple-500/20' : ''}
            ${uploading ? 'opacity-50 cursor-not-allowed' : ''}
          `}>
            <input {...getInputProps()} disabled={uploading} />
            <div className="text-4xl mb-2">📁</div>
            <div className="font-semibold">Upload File</div>
            <div className="text-sm text-purple-200">Select from device</div>
          </div>
        </div>

        {/* Rest of the existing upload UI */}
        {/* ... */}
      </motion.div>
    </div>
  );
};

export default VirtualTourUploadPage;
